            <h4>Previous Auctions</h4>
            <div className="admin-table-wrapper">
              <table className="admin-table">
                <thead>
                  <tr>
                    <th>Auction Name</th>
                    <th>Tournament</th>
                    <th>Date</th>
                    <th>Status</th>
                    <th>Actions</th>
                  </tr>
                </thead>
                <tbody>
                  {auctions.length === 0 && (
                    <tr><td colSpan="5" style={{textAlign:'center'}}>No auctions yet.</td></tr>
                  )}
                  {auctions.map(a => (
                    <tr key={a.id}>
                      <td>{a.auction_name}</td>
                      <td>{tournaments.find(t => t.id === a.tournament_id)?.name}</td>
                      <td>{new Date(a.auction_date).toLocaleString()}</td>
                      <td>{a.status}</td>
                      <td>
                        <div style={{ display: 'flex', gap: 8 }}>
                          <button 
                            className="btn-secondary" 
                            onClick={() => setCurrentAuction(a)}
                            style={{ padding: '5px 12px' }}
                          >
                            Select
                          </button>
                          <button 
                            className="btn-danger" 
                            onClick={() => handleDeleteAuction(a.id)}
                            disabled={loading}
                            style={{ padding: '5px 12px' }}
                          >
                            🗑️ Delete
                          </button>
                        </div>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        </div>
      )}

      {/* Team Management Tab */}
      {activeTab === 'team-management' && (
        <div>
          <h3>Team Management</h3>

          {/* Tournament and Team Selection */}
          <div style={{ marginBottom: 30, padding: 20, background: '#1a1a1a', borderRadius: 10 }}>
            <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: 20 }}>
              <div>
                <label style={{ display: 'block', marginBottom: 5 }}>Select Tournament</label>
                <select 
                  value={selectedTournamentForTeams}
                  onChange={(e) => {
                    setSelectedTournamentForTeams(e.target.value);
                    setSelectedTeamForPlayers('');
                  }}
                  style={{ width: '100%', padding: 10 }}
                >
                  <option value="">-- Select Tournament --</option>
                  {tournaments.map(t => (
                    <option key={t.id} value={t.id}>{t.name}</option>
                  ))}
                </select>
              </div>

              <div>
                <label style={{ display: 'block', marginBottom: 5 }}>Select Team</label>
                <select 
                  value={selectedTeamForPlayers}
                  onChange={(e) => {
                    setSelectedTeamForPlayers(e.target.value);
                    if (e.target.value && selectedTournamentForTeams) {
                      loadTeamSquads(selectedTournamentForTeams, e.target.value);
                    }
                  }}
                  style={{ width: '100%', padding: 10 }}
                  disabled={!selectedTournamentForTeams}
                >
                  <option value="">-- Select Team --</option>
                  {teams.filter(t => t.tournament_id === selectedTournamentForTeams).map(t => (
                    <option key={t.id} value={t.id}>
                      {t.name} ({t.players_count}/{tournaments.find(tour => tour.id === selectedTournamentForTeams)?.max_players_per_team})
                    </option>
                  ))}
                </select>
              </div>
            </div>
          </div>

          {/* Add Players */}
          {selectedTournamentForTeams && selectedTeamForPlayers && (
            <div style={{ marginBottom: 30 }}>
              <h4>Add Players to Team</h4>

              <div style={{ marginBottom: 20 }}>
                <label className="btn-secondary" style={{ cursor: 'pointer', marginRight: 10 }}>
                  Upload Players from CSV
                  <input 
                    type="file" 
                    accept=".csv,.xlsx,.xls"
                    onChange={handleUploadTeamCSV}
                    style={{ display: 'none' }}
                  />
                </label>
                <span style={{ color: '#888', fontSize: 14 }}>
                  CSV Format: player_name, jersey_number, player_role
                </span>
              </div>

              <div>
                <h5>Or Add Individual Players</h5>
                <div style={{ display: 'grid', gridTemplateColumns: 'repeat(3, 1fr)', gap: 10 }}>
                  {registeredPlayers.filter(p => p.status === 'registered').map(p => (
                    <div 
                      key={p.id} 
                      style={{ 
                        padding: 10, 
                        background: '#2a2a2a', 
                        borderRadius: 5,
                        display: 'flex',
                        justifyContent: 'space-between',
                        alignItems: 'center'
                      }}
                    >
                      <div>
                        <div>{p.name}</div>
                        <div style={{ fontSize: 12, color: '#888' }}>{p.cricket_role}</div>
                      </div>
                      <button 
                        className="btn-primary" 
                        onClick={() => handleAddPlayerToTeam(p.id)}
                        style={{ padding: '5px 10px', fontSize: 12 }}
                      >
                        Add
                      </button>
                    </div>
                  ))}
                </div>
              </div>
            </div>
          )}

          {/* Team Squad */}
          {teamSquads.length > 0 && (
            <div>
              <h4>Current Squad</h4>
              <div className="admin-table-wrapper">
                <table className="admin-table">
                  <thead>
                    <tr>
                      <th>Player Name</th>
                      <th>Role</th>
                      <th>Phone</th>
                      <th>Jersey #</th>
                      <th>Purchase Price</th>
                      <th>Position</th>
                      <th>Actions</th>
                    </tr>
                  </thead>
                  <tbody>
                    {teamSquads.map(sq => (
                      <tr key={sq.id}>
                        <td>{sq.player_registrations?.name}</td>
                        <td>{sq.player_registrations?.cricket_role}</td>
                        <td>{sq.player_registrations?.phone_number}</td>
                        <td>{sq.jersey_number || '-'}</td>
                        <td>₹{sq.purchase_price || 0}</td>
                        <td>
                          {sq.is_captain ? 'Captain' : sq.is_vice_captain ? 'Vice Captain' : sq.player_role || 'Player'}
                        </td>
                        <td>
                          <button 
                            className="btn-danger" 
                            onClick={() => handleRemovePlayerFromTeam(sq.id)}
                            disabled={loading}
                          >
                            Remove
                          </button>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default Admin;
