import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { supabase } from '../config/supabase';
import './Navbar.css';

// Simplified, mobile-friendly navbar with a top registration link.
const Navbar = () => {
  const [liveAuction, setLiveAuction] = useState(null);
  const [teams, setTeams] = useState([]);
  const [lastSoldPlayer, setLastSoldPlayer] = useState(null);

  useEffect(() => {
    loadLiveAuction();

    // Real-time subscription for auction updates
    const auctionChannel = supabase
      .channel('live-auction-updates')
      .on('postgres_changes', 
        { event: '*', schema: 'public', table: 'simple_auctions' }, 
        () => loadLiveAuction()
      )
      .on('postgres_changes',
        { event: '*', schema: 'public', table: 'simple_teams' },
        () => {
          if (liveAuction) loadTeams(liveAuction.id);
        }
      )
      .on('postgres_changes',
        { event: 'INSERT', schema: 'public', table: 'simple_player_sales' },
        (payload) => {
          if (payload.new.transaction_type === 'sold') {
            setLastSoldPlayer(payload.new);
            if (liveAuction) loadTeams(liveAuction.id);
          }
        }
      )
      .subscribe();

    return () => {
      auctionChannel.unsubscribe();
    };
  }, [liveAuction]);

  const loadLiveAuction = async () => {
    try {
      const { data, error } = await supabase
        .from('simple_auctions')
        .select('*')
        .eq('status', 'active')
        .order('created_at', { ascending: false })
        .limit(1);

      if (error) throw error;
      
      if (data && data.length > 0) {
        setLiveAuction(data[0]);
        loadTeams(data[0].id);
        loadLastSoldPlayer(data[0].id);
      } else {
        setLiveAuction(null);
        setTeams([]);
      }
    } catch (error) {
      console.error('Error loading live auction:', error);
    }
  };

  const loadTeams = async (auctionId) => {
    try {
      const { data, error } = await supabase
        .from('simple_teams')
        .select('*')
        .eq('auction_id', auctionId)
        .order('team_name', { ascending: true });

      if (error) throw error;
      setTeams(data || []);
    } catch (error) {
      console.error('Error loading teams:', error);
    }
  };

  const loadLastSoldPlayer = async (auctionId) => {
    try {
      const { data, error } = await supabase
        .from('simple_player_sales')
        .select('*')
        .eq('auction_id', auctionId)
        .eq('transaction_type', 'sold')
        .order('sale_time', { ascending: false })
        .limit(1);

      if (error) throw error;
      if (data && data.length > 0) {
        setLastSoldPlayer(data[0]);
      }
    } catch (error) {
      console.error('Error loading last sold player:', error);
    }
  };

  return (
    <>
      <div className="topbar">
        <div className="topbar-inner">
          <div className="topbar-left">Sankalp Premier League</div>
          <div className="topbar-right">
            <Link to="/register" className="register-link">Register</Link>
          </div>
        </div>
      </div>

      {/* Live Auction Banner */}
      {liveAuction && (
        <div className="live-auction-banner">
          <div className="live-auction-content">
            <div className="auction-title">
              <span className="live-indicator">ðŸ”´ LIVE</span>
              <span className="tournament-name">{liveAuction.tournament_name}</span>
            </div>
            
            {lastSoldPlayer && (
              <div className="last-sold">
                <span className="label">Last Sold:</span>
                <span className="player-name">{lastSoldPlayer.player_name}</span>
                <span className="to-team">â†’ {lastSoldPlayer.team_name}</span>
                <span className="price">â‚¹{lastSoldPlayer.sale_price?.toLocaleString()}</span>
              </div>
            )}

            <div className="teams-budget-scroll">
              {teams.map(team => (
                <div key={team.id} className="team-budget-item">
                  <span className="team-name">{team.team_name}</span>
                  <span className="budget">â‚¹{team.remaining_budget?.toLocaleString()}</span>
                  <span className="players">({team.players_count || 0})</span>
                </div>
              ))}
            </div>
          </div>
        </div>
      )}

      <nav className="navbar">
        <div className="navbar-brand">
          <div className="logo">
            <img src="/spl.jpg" alt="SPL Logo" className="navbar-logo-img" />
          </div>
          <h1 className="navbar-title">Sankalp Premier League</h1>
        </div>
        <div className="navbar-links">
          <Link to="/" className="nav-link">Home</Link>
          <Link to="/about-us" className="nav-link">About</Link>
          <Link to="/watch-live" className="nav-link">Live</Link>
          <Link to="/latest-news" className="nav-link">News</Link>
          <Link to="/live-auction-public" className="nav-link">Auction</Link>
          <Link to="/scoring" className="nav-link">Scoring</Link>
          <Link to="/schedule" className="nav-link">Schedule</Link>
          <Link to="/admin" className="nav-link">Admin</Link>
        </div>
      </nav>
    </>
  );
};

export default Navbar;
