    {
      id: 5,
      title: 'Record Breaking Opening Ceremony',
      excerpt: 'The SPL opening ceremony witnessed record attendance with spectacular performances and team introductions.',
      category: 'Event',
      date: '2024-01-12',
      readTime: '2 min read',
      image: 'ðŸŽ‰'
    },
