import React, { useState, useEffect } from 'react';
import { supabase } from '../config/supabase';
import './Home.css';

const LiveAuctionPublic = () => {
  const [tournaments, setTournaments] = useState([]);
  const [selectedTournament, setSelectedTournament] = useState('');
  const [teams, setTeams] = useState([]);
  const [auctions, setAuctions] = useState([]);
  const [selectedAuction, setSelectedAuction] = useState('');
  const [currentAuctionData, setCurrentAuctionData] = useState(null);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    loadTournaments();
    
    // Set up real-time subscription for team budget updates
    const subscription = supabase
      .channel('teams-changes')
      .on('postgres_changes', 
        { event: '*', schema: 'public', table: 'teams' }, 
        () => {
          if (selectedTournament) {
            loadTeams(selectedTournament);
          }
        }
      )
      .subscribe();

    return () => {
      subscription.unsubscribe();
    };
  }, [selectedTournament]);

  const loadTournaments = async () => {
    try {
      const { data, error } = await supabase
        .from('tournaments')
        .select('*')
        .order('created_at', { ascending: false });

      if (error) throw error;
      setTournaments(data || []);
    } catch (error) {
      console.error('Error loading tournaments:', error);
    }
  };

  const loadTeams = async (tournamentId) => {
    setLoading(true);
    try {
      const { data, error } = await supabase
        .from('teams')
        .select('*')
        .eq('tournament_id', tournamentId)
        .order('name', { ascending: true });

      if (error) throw error;
      setTeams(data || []);
    } catch (error) {
      console.error('Error loading teams:', error);
    } finally {
      setLoading(false);
    }
  };

  const loadAuctions = async (tournamentId) => {
    try {
      const { data, error } = await supabase
        .from('live_auctions')
        .select('*')
        .eq('tournament_id', tournamentId)
        .order('created_at', { ascending: false });

      if (error) throw error;
      setAuctions(data || []);
    } catch (error) {
      console.error('Error loading auctions:', error);
    }
  };

  const loadAuctionData = async (auctionId) => {
    setLoading(true);
    try {
      const { data, error } = await supabase
        .from('live_auctions')
        .select('*')
        .eq('id', auctionId)
        .single();

      if (error) throw error;
      setCurrentAuctionData(data);
    } catch (error) {
      console.error('Error loading auction data:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleTournamentChange = (tournamentId) => {
    setSelectedTournament(tournamentId);
    setTeams([]);
    setAuctions([]);
    setSelectedAuction('');
    setCurrentAuctionData(null);
    
    if (tournamentId) {
      loadTeams(tournamentId);
      loadAuctions(tournamentId);
    }
  };

  const handleAuctionChange = (auctionId) => {
    setSelectedAuction(auctionId);
    if (auctionId) {
      loadAuctionData(auctionId);
    } else {
      setCurrentAuctionData(null);
    }
  };

  return (
    <div className="container" style={{ padding: '40px 20px' }}>
      <h1 style={{ textAlign: 'center', marginBottom: 40, color: '#00ff00' }}>
        Live Auction
      </h1>

      {/* Tournament Selection */}
      <div style={{ maxWidth: 600, margin: '0 auto 40px' }}>
        <label style={{ display: 'block', marginBottom: 10, fontSize: 18 }}>
          Select Tournament
        </label>
        <select 
          value={selectedTournament}
          onChange={(e) => handleTournamentChange(e.target.value)}
          style={{ 
            width: '100%', 
            padding: 15, 
            fontSize: 16,
            background: '#1a1a1a',
            color: '#fff',
            border: '2px solid #333',
            borderRadius: 8
          }}
        >
          <option value="">-- Select Tournament --</option>
          {tournaments.map(t => (
            <option key={t.id} value={t.id}>{t.name}</option>
          ))}
        </select>
      </div>

      {/* Auction Selection */}
      {selectedTournament && auctions.length > 0 && (
        <div style={{ maxWidth: 600, margin: '0 auto 40px' }}>
          <label style={{ display: 'block', marginBottom: 10, fontSize: 18 }}>
            Select Auction
          </label>
          <select 
            value={selectedAuction}
            onChange={(e) => handleAuctionChange(e.target.value)}
            style={{ 
              width: '100%', 
              padding: 15, 
              fontSize: 16,
              background: '#1a1a1a',
              color: '#fff',
              border: '2px solid #333',
              borderRadius: 8
            }}
          >
            <option value="">-- Select Auction --</option>
            {auctions.map(a => (
              <option key={a.id} value={a.id}>
                {a.auction_name} - {new Date(a.auction_date).toLocaleDateString()}
              </option>
            ))}
          </select>
        </div>
      )}

      {/* Team Purses Display */}
      {selectedTournament && teams.length > 0 && (
        <div style={{ marginBottom: 40 }}>
          <h2 style={{ textAlign: 'center', marginBottom: 30 }}>Team Purses</h2>
          
          <div style={{ 
            display: 'grid', 
            gridTemplateColumns: 'repeat(auto-fit, minmax(250px, 1fr))',
            gap: 20,
            maxWidth: 1200,
            margin: '0 auto'
          }}>
            {teams.map(team => (
              <div 
                key={team.id}
                style={{
                  padding: 25,
                  background: 'linear-gradient(135deg, #1a1a1a 0%, #2a2a2a 100%)',
                  borderRadius: 15,
                  border: '2px solid #333',
                  textAlign: 'center',
                  boxShadow: '0 4px 6px rgba(0,0,0,0.3)'
                }}
              >
                <h3 style={{ 
                  marginBottom: 15, 
                  fontSize: 20,
                  color: '#00ff00',
                  textTransform: 'uppercase'
                }}>
                  {team.name}
                </h3>
                
                <div style={{ marginBottom: 10 }}>
                  <div style={{ fontSize: 14, color: '#888', marginBottom: 5 }}>
                    Remaining Budget
                  </div>
                  <div style={{ 
                    fontSize: 32, 
                    fontWeight: 'bold',
                    color: team.remaining_budget > 50000 ? '#00ff00' : 
                           team.remaining_budget > 20000 ? '#ffaa00' : '#ff4444'
                  }}>
                    ₹{team.remaining_budget?.toLocaleString()}
                  </div>
                </div>

                <div style={{ 
                  display: 'grid', 
                  gridTemplateColumns: '1fr 1fr',
                  gap: 10,
                  marginTop: 15,
                  paddingTop: 15,
                  borderTop: '1px solid #333'
                }}>
                  <div>
                    <div style={{ fontSize: 12, color: '#888' }}>Players</div>
                    <div style={{ fontSize: 18, fontWeight: 'bold' }}>
                      {team.players_count || 0}
                    </div>
                  </div>
                  <div>
                    <div style={{ fontSize: 12, color: '#888' }}>Spent</div>
                    <div style={{ fontSize: 18, fontWeight: 'bold' }}>
                      ₹{((team.current_budget || 100000) - (team.remaining_budget || 0)).toLocaleString()}
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      )}

      {/* Current Auction Info */}
      {currentAuctionData && (
        <div style={{ 
          maxWidth: 800, 
          margin: '0 auto',
          padding: 30,
          background: '#1a1a1a',
          borderRadius: 15,
          border: '2px solid #00ff00'
        }}>
          <h2 style={{ textAlign: 'center', color: '#00ff00', marginBottom: 20 }}>
            {currentAuctionData.auction_name}
          </h2>
          
          <div style={{ 
            display: 'grid', 
            gridTemplateColumns: 'repeat(2, 1fr)',
            gap: 20,
            textAlign: 'center'
          }}>
            <div>
              <div style={{ fontSize: 14, color: '#888', marginBottom: 5 }}>
                Status
              </div>
              <div style={{ 
                fontSize: 20, 
                fontWeight: 'bold',
                color: currentAuctionData.status === 'live' ? '#00ff00' : '#ffaa00',
                textTransform: 'uppercase'
              }}>
                {currentAuctionData.status}
              </div>
            </div>

            <div>
              <div style={{ fontSize: 14, color: '#888', marginBottom: 5 }}>
                Date
              </div>
              <div style={{ fontSize: 20, fontWeight: 'bold' }}>
                {new Date(currentAuctionData.auction_date).toLocaleDateString()}
              </div>
            </div>

            <div>
              <div style={{ fontSize: 14, color: '#888', marginBottom: 5 }}>
                Players Sold
              </div>
              <div style={{ fontSize: 20, fontWeight: 'bold', color: '#00ff00' }}>
                {currentAuctionData.total_players_sold || 0}
              </div>
            </div>

            <div>
              <div style={{ fontSize: 14, color: '#888', marginBottom: 5 }}>
                Total Amount
              </div>
              <div style={{ fontSize: 20, fontWeight: 'bold', color: '#00ff00' }}>
                ₹{(currentAuctionData.total_amount_spent || 0).toLocaleString()}
              </div>
            </div>
          </div>
        </div>
      )}

      {loading && (
        <div style={{ textAlign: 'center', padding: 40, color: '#00ff00' }}>
          Loading...
        </div>
      )}

      {!selectedTournament && (
        <div style={{ textAlign: 'center', padding: 60, color: '#888' }}>
          <h3>Select a tournament to view auction details</h3>
        </div>
      )}
    </div>
  );
};

export default LiveAuctionPublic;
