import React, { useState, useEffect } from 'react';
import { supabase } from '../config/supabase';
import './Home.css';

const LiveAuctionPublic = () => {
  const [tournaments, setTournaments] = useState([]);
  const [selectedTournament, setSelectedTournament] = useState('');
  const [teams, setTeams] = useState([]);
  const [boughtPlayers, setBoughtPlayers] = useState([]);
  const [unsoldPlayers, setUnsoldPlayers] = useState([]);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    loadTournaments();
    
    // Set up real-time subscription for team budget updates
    const subscription = supabase
      .channel('teams-changes')
      .on('postgres_changes', 
        { event: '*', schema: 'public', table: 'teams' }, 
        () => {
          if (selectedTournament) {
            loadTeams(selectedTournament);
          }
        }
      )
      .on('postgres_changes',
        { event: '*', schema: 'public', table: 'team_squads' },
        () => {
          if (selectedTournament) {
            loadBoughtPlayers(selectedTournament);
          }
        }
      )
      .subscribe();

    return () => {
      subscription.unsubscribe();
    };
  }, [selectedTournament]);

  const loadTournaments = async () => {
    try {
      const { data, error } = await supabase
        .from('tournaments')
        .select('*')
        .order('created_at', { ascending: false });

      if (error) throw error;
      setTournaments(data || []);
    } catch (error) {
      console.error('Error loading tournaments:', error);
    }
  };

  const loadTeams = async (tournamentId) => {
    setLoading(true);
    try {
      const { data, error } = await supabase
        .from('teams')
        .select('*')
        .eq('tournament_id', tournamentId)
        .order('name', { ascending: true });

      if (error) throw error;
      setTeams(data || []);
    } catch (error) {
      console.error('Error loading teams:', error);
    } finally {
      setLoading(false);
    }
  };

  const loadBoughtPlayers = async (tournamentId) => {
    setLoading(true);
    try {
      const { data, error } = await supabase
        .from('team_squads')
        .select(`
          *,
          teams:team_id (name),
          player_registrations_supabase:player_id (name, mobile, category)
        `)
        .eq('tournament_id', tournamentId)
        .order('price', { ascending: false });

      if (error) throw error;
      setBoughtPlayers(data || []);
    } catch (error) {
      console.error('Error loading bought players:', error);
    } finally {
      setLoading(false);
    }
  };

  const loadUnsoldPlayers = async (tournamentId) => {
    setLoading(true);
    try {
      // Get all players from auction_transactions with 'unsold' status
      const { data, error } = await supabase
        .from('auction_transactions')
        .select(`
          *,
          player_registrations_supabase:player_id (name, mobile, category)
        `)
        .eq('tournament_id', tournamentId)
        .eq('status', 'unsold')
        .order('created_at', { ascending: false });

      if (error) throw error;
      setUnsoldPlayers(data || []);
    } catch (error) {
      console.error('Error loading unsold players:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleTournamentChange = (tournamentId) => {
    setSelectedTournament(tournamentId);
    setTeams([]);
    setBoughtPlayers([]);
    setUnsoldPlayers([]);
    
    if (tournamentId) {
      loadTeams(tournamentId);
      loadBoughtPlayers(tournamentId);
      loadUnsoldPlayers(tournamentId);
    }
  };

  return (
    <div className="container" style={{ padding: '40px 20px', minHeight: '100vh' }}>
      <h1 style={{ 
        textAlign: 'center', 
        marginBottom: 40, 
        color: '#00f2fe',
        fontSize: '2.8rem',
        fontFamily: '"Orbitron", sans-serif',
        textShadow: '0 0 20px rgba(0, 242, 254, 0.5)'
      }}>
        Live Auction View
      </h1>

      {/* Tournament Selection */}
      <div style={{ 
        maxWidth: 600, 
        margin: '0 auto 40px',
        background: 'rgba(26, 26, 62, 0.6)',
        backdropFilter: 'blur(20px)',
        padding: '30px',
        borderRadius: '15px',
        border: '1px solid rgba(168, 85, 247, 0.3)'
      }}>
        <label style={{ 
          display: 'block', 
          marginBottom: 15, 
          fontSize: 18,
          color: '#e9f6ff',
          fontWeight: 600
        }}>
          Select Tournament
        </label>
        <select 
          value={selectedTournament}
          onChange={(e) => handleTournamentChange(e.target.value)}
          style={{ 
            width: '100%', 
            padding: 15, 
            fontSize: 16,
            background: 'rgba(10, 10, 31, 0.8)',
            color: '#ffffff',
            border: '2px solid rgba(168, 85, 247, 0.5)',
            borderRadius: 8,
            outline: 'none',
            cursor: 'pointer',
            transition: 'all 0.3s ease'
          }}
        >
          <option value="">-- Select Tournament --</option>
          {tournaments.map(t => (
            <option key={t.id} value={t.id}>{t.name}</option>
          ))}
        </select>
      </div>

      {/* Team Purses Display */}
      {selectedTournament && teams.length > 0 && (
        <div style={{ marginBottom: 50 }}>
          <h2 style={{ 
            textAlign: 'center', 
            marginBottom: 30,
            color: '#00f2fe',
            fontSize: '2rem',
            fontFamily: '"Orbitron", sans-serif'
          }}>
            Team Purses
          </h2>
          
          <div style={{ 
            display: 'grid', 
            gridTemplateColumns: 'repeat(auto-fit, minmax(280px, 1fr))',
            gap: 25,
            maxWidth: 1400,
            margin: '0 auto'
          }}>
            {teams.map(team => (
              <div 
                key={team.id}
                style={{
                  padding: 30,
                  background: 'linear-gradient(135deg, rgba(26, 26, 62, 0.8) 0%, rgba(42, 42, 82, 0.6) 100%)',
                  backdropFilter: 'blur(20px)',
                  borderRadius: 20,
                  border: '2px solid rgba(168, 85, 247, 0.3)',
                  textAlign: 'center',
                  boxShadow: '0 8px 32px rgba(0, 242, 254, 0.15)',
                  transition: 'all 0.3s ease',
                  cursor: 'pointer'
                }}
                onMouseEnter={(e) => {
                  e.currentTarget.style.transform = 'translateY(-5px)';
                  e.currentTarget.style.boxShadow = '0 12px 40px rgba(168, 85, 247, 0.4)';
                  e.currentTarget.style.borderColor = 'rgba(168, 85, 247, 0.8)';
                }}
                onMouseLeave={(e) => {
                  e.currentTarget.style.transform = 'translateY(0)';
                  e.currentTarget.style.boxShadow = '0 8px 32px rgba(0, 242, 254, 0.15)';
                  e.currentTarget.style.borderColor = 'rgba(168, 85, 247, 0.3)';
                }}
              >
                <h3 style={{ 
                  marginBottom: 20, 
                  fontSize: 22,
                  color: '#00f2fe',
                  textTransform: 'uppercase',
                  fontFamily: '"Orbitron", sans-serif',
                  letterSpacing: '2px',
                  textShadow: '0 0 10px rgba(0, 242, 254, 0.5)'
                }}>
                  {team.name}
                </h3>
                
                <div style={{ marginBottom: 15 }}>
                  <div style={{ 
                    fontSize: 14, 
                    color: '#a0aec0', 
                    marginBottom: 8,
                    textTransform: 'uppercase',
                    letterSpacing: '1px'
                  }}>
                    Remaining Budget
                  </div>
                  <div style={{ 
                    fontSize: 36, 
                    fontWeight: 'bold',
                    background: team.remaining_budget > 50000 ? 
                      'linear-gradient(135deg, #10b981 0%, #00f2fe 100%)' : 
                      team.remaining_budget > 20000 ? 
                      'linear-gradient(135deg, #fbbf24 0%, #f59e0b 100%)' : 
                      'linear-gradient(135deg, #ef4444 0%, #dc2626 100%)',
                    WebkitBackgroundClip: 'text',
                    WebkitTextFillColor: 'transparent',
                    backgroundClip: 'text'
                  }}>
                    ₹{team.remaining_budget?.toLocaleString()}
                  </div>
                </div>

                <div style={{ 
                  display: 'grid', 
                  gridTemplateColumns: '1fr 1fr',
                  gap: 15,
                  marginTop: 20,
                  paddingTop: 20,
                  borderTop: '1px solid rgba(168, 85, 247, 0.3)'
                }}>
                  <div style={{
                    padding: '10px',
                    background: 'rgba(168, 85, 247, 0.1)',
                    borderRadius: '10px'
                  }}>
                    <div style={{ 
                      fontSize: 12, 
                      color: '#a0aec0',
                      textTransform: 'uppercase',
                      marginBottom: 5
                    }}>Players</div>
                    <div style={{ 
                      fontSize: 24, 
                      fontWeight: 'bold',
                      color: '#e9f6ff'
                    }}>
                      {team.players_count || 0}
                    </div>
                  </div>
                  <div style={{
                    padding: '10px',
                    background: 'rgba(0, 242, 254, 0.1)',
                    borderRadius: '10px'
                  }}>
                    <div style={{ 
                      fontSize: 12, 
                      color: '#a0aec0',
                      textTransform: 'uppercase',
                      marginBottom: 5
                    }}>Spent</div>
                    <div style={{ 
                      fontSize: 24, 
                      fontWeight: 'bold',
                      color: '#e9f6ff'
                    }}>
                      ₹{((team.current_budget || 100000) - (team.remaining_budget || 0)).toLocaleString()}
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      )}

      {/* Bought Players Section */}
      {selectedTournament && boughtPlayers.length > 0 && (
        <div style={{ marginBottom: 50 }}>
          <h2 style={{ 
            textAlign: 'center', 
            marginBottom: 30,
            color: '#10b981',
            fontSize: '2rem',
            fontFamily: '"Orbitron", sans-serif',
            textShadow: '0 0 20px rgba(16, 185, 129, 0.5)'
          }}>
            Bought Players ({boughtPlayers.length})
          </h2>
          
          <div style={{
            maxWidth: 1400,
            margin: '0 auto',
            background: 'rgba(26, 26, 62, 0.6)',
            backdropFilter: 'blur(20px)',
            borderRadius: 20,
            padding: 30,
            border: '1px solid rgba(16, 185, 129, 0.3)'
          }}>
            <div style={{
              display: 'grid',
              gridTemplateColumns: 'repeat(auto-fill, minmax(300px, 1fr))',
              gap: 20
            }}>
              {boughtPlayers.map((player, index) => (
                <div 
                  key={index}
                  style={{
                    padding: 20,
                    background: 'linear-gradient(135deg, rgba(16, 185, 129, 0.1) 0%, rgba(0, 242, 254, 0.05) 100%)',
                    borderRadius: 15,
                    border: '1px solid rgba(16, 185, 129, 0.3)',
                    transition: 'all 0.3s ease'
                  }}
                  onMouseEnter={(e) => {
                    e.currentTarget.style.transform = 'translateY(-3px)';
                    e.currentTarget.style.borderColor = 'rgba(16, 185, 129, 0.6)';
                  }}
                  onMouseLeave={(e) => {
                    e.currentTarget.style.transform = 'translateY(0)';
                    e.currentTarget.style.borderColor = 'rgba(16, 185, 129, 0.3)';
                  }}
                >
                  <div style={{
                    fontSize: 18,
                    fontWeight: 'bold',
                    color: '#e9f6ff',
                    marginBottom: 10
                  }}>
                    {player.player_registrations_supabase?.name || 'Unknown Player'}
                  </div>
                  
                  <div style={{
                    display: 'grid',
                    gap: 8,
                    fontSize: 14,
                    color: '#a0aec0'
                  }}>
                    <div style={{ display: 'flex', justifyContent: 'space-between' }}>
                      <span>Team:</span>
                      <span style={{ color: '#00f2fe', fontWeight: 600 }}>
                        {player.teams?.name || 'Unknown Team'}
                      </span>
                    </div>
                    <div style={{ display: 'flex', justifyContent: 'space-between' }}>
                      <span>Price:</span>
                      <span style={{ 
                        color: '#10b981', 
                        fontWeight: 'bold',
                        fontSize: 16
                      }}>
                        ₹{player.price?.toLocaleString()}
                      </span>
                    </div>
                    {player.player_registrations_supabase?.category && (
                      <div style={{ display: 'flex', justifyContent: 'space-between' }}>
                        <span>Category:</span>
                        <span style={{ 
                          color: '#a855f7',
                          fontWeight: 600
                        }}>
                          ₹{player.player_registrations_supabase.category.toLocaleString()}
                        </span>
                      </div>
                    )}
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>
      )}

      {/* Unsold Players Section */}
      {selectedTournament && unsoldPlayers.length > 0 && (
        <div style={{ marginBottom: 50 }}>
          <h2 style={{ 
            textAlign: 'center', 
            marginBottom: 30,
            color: '#ef4444',
            fontSize: '2rem',
            fontFamily: '"Orbitron", sans-serif',
            textShadow: '0 0 20px rgba(239, 68, 68, 0.5)'
          }}>
            Unsold Players ({unsoldPlayers.length})
          </h2>
          
          <div style={{
            maxWidth: 1400,
            margin: '0 auto',
            background: 'rgba(26, 26, 62, 0.6)',
            backdropFilter: 'blur(20px)',
            borderRadius: 20,
            padding: 30,
            border: '1px solid rgba(239, 68, 68, 0.3)'
          }}>
            <div style={{
              display: 'grid',
              gridTemplateColumns: 'repeat(auto-fill, minmax(250px, 1fr))',
              gap: 20
            }}>
              {unsoldPlayers.map((player, index) => (
                <div 
                  key={index}
                  style={{
                    padding: 20,
                    background: 'linear-gradient(135deg, rgba(239, 68, 68, 0.1) 0%, rgba(220, 38, 38, 0.05) 100%)',
                    borderRadius: 15,
                    border: '1px solid rgba(239, 68, 68, 0.3)',
                    transition: 'all 0.3s ease'
                  }}
                  onMouseEnter={(e) => {
                    e.currentTarget.style.transform = 'translateY(-3px)';
                    e.currentTarget.style.borderColor = 'rgba(239, 68, 68, 0.6)';
                  }}
                  onMouseLeave={(e) => {
                    e.currentTarget.style.transform = 'translateY(0)';
                    e.currentTarget.style.borderColor = 'rgba(239, 68, 68, 0.3)';
                  }}
                >
                  <div style={{
                    fontSize: 18,
                    fontWeight: 'bold',
                    color: '#e9f6ff',
                    marginBottom: 10
                  }}>
                    {player.player_registrations_supabase?.name || 'Unknown Player'}
                  </div>
                  
                  <div style={{
                    display: 'grid',
                    gap: 8,
                    fontSize: 14,
                    color: '#a0aec0'
                  }}>
                    {player.player_registrations_supabase?.mobile && (
                      <div style={{ display: 'flex', justifyContent: 'space-between' }}>
                        <span>Mobile:</span>
                        <span style={{ color: '#e9f6ff' }}>
                          {player.player_registrations_supabase.mobile}
                        </span>
                      </div>
                    )}
                    {player.player_registrations_supabase?.category && (
                      <div style={{ display: 'flex', justifyContent: 'space-between' }}>
                        <span>Base Price:</span>
                        <span style={{ 
                          color: '#a855f7',
                          fontWeight: 600
                        }}>
                          ₹{player.player_registrations_supabase.category.toLocaleString()}
                        </span>
                      </div>
                    )}
                    <div style={{
                      marginTop: 5,
                      padding: '5px 10px',
                      background: 'rgba(239, 68, 68, 0.2)',
                      borderRadius: 5,
                      textAlign: 'center',
                      color: '#ef4444',
                      fontWeight: 600,
                      textTransform: 'uppercase',
                      fontSize: 12
                    }}>
                      Unsold
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>
      )}

      {loading && (
        <div style={{ 
          textAlign: 'center', 
          padding: 60,
          color: '#00f2fe',
          fontSize: '1.5rem',
          fontFamily: '"Orbitron", sans-serif'
        }}>
          <div className="spinner" style={{
            width: '50px',
            height: '50px',
            border: '4px solid rgba(168, 85, 247, 0.3)',
            borderTop: '4px solid #00f2fe',
            borderRadius: '50%',
            margin: '0 auto 20px',
            animation: 'spin 1s linear infinite'
          }}></div>
          Loading...
        </div>
      )}

      {!selectedTournament && (
        <div style={{ 
          textAlign: 'center', 
          padding: 80,
          background: 'rgba(26, 26, 62, 0.4)',
          borderRadius: 20,
          border: '1px dashed rgba(168, 85, 247, 0.3)',
          maxWidth: 600,
          margin: '0 auto'
        }}>
          <div style={{
            fontSize: '3rem',
            marginBottom: 20
          }}>🏏</div>
          <h3 style={{
            color: '#e9f6ff',
            fontSize: '1.5rem',
            fontFamily: '"Orbitron", sans-serif',
            marginBottom: 10
          }}>
            Select a Tournament
          </h3>
          <p style={{ color: '#a0aec0' }}>
            Choose a tournament from the dropdown above to view auction details
          </p>
        </div>
      )}
    </div>
  );
};

export default LiveAuctionPublic;
