import React, { useState, useEffect } from 'react';
import * as XLSX from 'xlsx';
import { supabase } from '../config/supabase';
import AdminLogin from './AdminLogin';
import './Home.css';

const LOCAL_KEY = 'splRegisteredPlayers';

const Admin = () => {
  const [players, setPlayers] = useState([]);
  const [tournaments, setTournaments] = useState([]);
  const [schedules, setSchedules] = useState([]);
  const [activeTab, setActiveTab] = useState('players'); // 'players', 'tournaments', 'schedules'
  const [editIndex, setEditIndex] = useState(null);
  const [editForm, setEditForm] = useState({});
  const [editingTournament, setEditingTournament] = useState(null);
  const [editingSchedule, setEditingSchedule] = useState(null);
  const [loading, setLoading] = useState(false);
  const [loggedIn, setLoggedIn] = useState(() => sessionStorage.getItem('splAdminLoggedIn') === 'true');

  // Load data from Supabase
  useEffect(() => {
    if (loggedIn) {
      loadPlayersFromSupabase();
      loadTournamentsFromSupabase();
      loadSchedulesFromSupabase();

      // Also load from localStorage as fallback
      const data = localStorage.getItem(LOCAL_KEY);
      if (data) {
        const localPlayers = JSON.parse(data);
        setPlayers(prev => {
          const supabasePlayerNames = prev.map(p => p.name);
          const uniqueLocalPlayers = localPlayers.filter(lp => !supabasePlayerNames.includes(lp.name));
          return [...prev, ...uniqueLocalPlayers];
        });
      }
    }
  }, [loggedIn]);

  // Load Players from Supabase
  const loadPlayersFromSupabase = async () => {
    setLoading(true);
    try {
      const { data, error } = await supabase
        .from('players_registered')
        .select('*')
        .order('created_at', { ascending: false });

      if (error) throw error;
      setPlayers(data || []);
    } catch (error) {
      console.error('Error loading players:', error);
      alert('Error loading players from database: ' + error.message);
    } finally {
      setLoading(false);
    }
  };

  // Load Tournaments from Supabase
  const loadTournamentsFromSupabase = async () => {
    setLoading(true);
    try {
      const { data, error } = await supabase
        .from('tournaments')
        .select('*')
        .order('created_at', { ascending: false });

      if (error) throw error;
      setTournaments(data || []);
    } catch (error) {
      console.error('Error loading tournaments:', error);
      alert('Error loading tournaments from database: ' + error.message);
    } finally {
      setLoading(false);
    }
  };

  // Load Schedules from Supabase
  const loadSchedulesFromSupabase = async () => {
    setLoading(true);
    try {
      const { data, error } = await supabase
        .from('tournament_schedule')
        .select('*')
        .order('match_date', { ascending: true });

      if (error) throw error;
      setSchedules(data || []);
    } catch (error) {
      console.error('Error loading schedules:', error);
      alert('Error loading schedules from database: ' + error.message);
    } finally {
      setLoading(false);
    }
  };

  const handleEdit = (idx) => {
    setEditIndex(idx);
    setEditForm(players[idx]);
  };

  const handleEditChange = (e) => {
    setEditForm({ ...editForm, [e.target.name]: e.target.value });
  };

  const handleEditSave = () => {
    const updated = [...players];
    updated[editIndex] = editForm;
    setPlayers(updated);
    localStorage.setItem(LOCAL_KEY, JSON.stringify(updated));
    setEditIndex(null);
  };

  const handleDelete = (idx) => {
    if (window.confirm('Delete this player?')) {
      const updated = players.filter((_, i) => i !== idx);
      setPlayers(updated);
      localStorage.setItem(LOCAL_KEY, JSON.stringify(updated));
    }
  };

  const downloadExcel = () => {
    const ws = XLSX.utils.json_to_sheet(players);
    const wb = XLSX.utils.book_new();
    XLSX.utils.book_append_sheet(wb, ws, 'Players');
    XLSX.writeFile(wb, `SPL_Registered_Players_${new Date().toISOString().split('T')[0]}.xlsx`);
  };

  const handleLogin = () => {
    sessionStorage.setItem('splAdminLoggedIn', 'true');
    setLoggedIn(true);
  };

  if (!loggedIn) {
    return <AdminLogin onLogin={handleLogin} />;
  }

  return (
    <div className="admin-section">
      <h2>Registered Players</h2>
      <button className="btn-primary" onClick={downloadExcel} style={{marginBottom:16}}>Download Excel</button>
      <div className="admin-table-wrapper">
        <table className="admin-table">
          <thead>
            <tr>
              <th>Name</th>
              <th>Age</th>
              <th>Phone</th>
              <th>Building</th>
              <th>Wing</th>
              <th>Flat</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {players.length === 0 && (
              <tr><td colSpan="7" style={{textAlign:'center'}}>No registrations yet.</td></tr>
            )}
            {players.map((p, idx) => (
              <tr key={idx}>
                {editIndex === idx ? (
                  <>
                    <td><input name="name" value={editForm.name} onChange={handleEditChange} /></td>
                    <td><input name="age" value={editForm.age} onChange={handleEditChange} /></td>
                    <td><input name="phone" value={editForm.phone} onChange={handleEditChange} /></td>
                    <td><input name="building" value={editForm.building} onChange={handleEditChange} /></td>
                    <td><input name="wing" value={editForm.wing} onChange={handleEditChange} /></td>
                    <td><input name="flat" value={editForm.flat} onChange={handleEditChange} /></td>
                    <td>
                      <button className="btn-primary" onClick={handleEditSave}>Save</button>
                      <button className="btn-secondary" onClick={()=>setEditIndex(null)}>Cancel</button>
                    </td>
                  </>
                ) : (
                  <>
                    <td>{p.name}</td>
                    <td>{p.age}</td>
                    <td>{p.phone}</td>
                    <td>{p.building}</td>
                    <td>{p.wing}</td>
                    <td>{p.flat}</td>
                    <td>
                      <button className="btn-secondary" onClick={()=>handleEdit(idx)}>Edit</button>
                      <button className="btn-danger" onClick={()=>handleDelete(idx)}>Delete</button>
                    </td>
                  </>
                )}
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default Admin;
